import {
    ModalPopup,
    EventDispatcher,
    ModalPopupEvents,
    IModalPopupStyles,
    ModalPopupVO,
} from 'general-framework';

// example data to init popup
const data: ModalPopupVO = {
    header: 'Test popup header',
    body: ['Test popup body'],
    buttons: [
        {
            class: 'danger',
            text: 'exit game',
            callback: () => {}
        },
        {
            class: 'primary',
            text: 'history',
            callback: () => {}
        },
        {
            class: 'success',
            text: 'continue',
            callback: () => {}
        },
    ],
    isCloseBtnShown: true,
};

// create your own styles to customize popup
const styles: IModalPopupStyles = {
    background: '#000',
    color: '#fff',
    btnColor: '#c3c3c3',
};

const dispatcher = EventDispatcher.getInstance();

// context
// initialization classes that needed to use realty check
const modal = new ModalPopup();

// insert styles for popup into html head
// run it once on a project!
// add your custom styles here
modal.insertPopupStyles(styles);

dispatcher.dispatch(ModalPopupEvents.SHOW_MODAL_POPUP, data);

