/**
 * interface for data to load sounds by theirs priority
 * we have 3 priority groups: preload, initial, lazy
 */
export interface ISoundsLoader {
    groups: {
        general: ISoundsGroup[];
        initial: ISoundsGroup[];
        preload: ISoundsGroup[];
        lazy: ISoundsGroup[];
    };
}
/**
 * interface for data to load each atlas
 * @param group - is array of groups (for example bigWin, freeSpin, bonusGame)
 * loader collects all sounds by its groups, then load each group one by one
 */
export interface ISoundsGroup {
    id: string;
    url: string;
    type: string;
    group: string[];
}
