/**
 * interface for data to load bitmapFonts by theirs priority
 * we have 3 priority groups: preload, initial, lazy
 */
export interface IBitmapFontsLoader {
    groups: {
        initial: IBitmapFontGroup[], preload: IBitmapFontGroup[], lazy: IBitmapFontGroup[]
    }
}

/**
 * interface for data to load each atlas
 * @param group - is array of groups (for example bigWin, freeSpin, bonusGame)
 * loader collects all bitmapFonts by its groups, then load each group one by one
 */
export interface IBitmapFontGroup {
    id: string,
    fontUrl: string,
    atlasUrl: string,
    group: string[]
}
