/**
 * Interface with a list of style props for customization modal popup
 */
export interface IModalPopupStyles {
    background?: string; // color or image of popup background
    color?: string; // message color (header and body)
    fontStyle?: string; // font style (header and body) (italic)
    primaryBackground?: string; // background primary btn color
    successBackground?: string; // background success btn color
    dangerBackground?: string; // background danger btn color
    warningBackground?: string; // background warning btn color
    bgrBorderRadius?: string; // container radius in px
    btnFontColor?: string; // text btn color
    btnBorderRadius?: string; // button radius in px
    btnFontSize?: string; // font size for buttons in px
}
