import {SlotEventDispatcher} from "./eventDispatcher/SlotEventDispatcher";
import {getDispatcher} from "./createInstance";

/**
 * Base controller for calculating game data or for core job.
 * Only controllers, states have access to models.
 * Use dispatcher to work with actions.
 * Use getModel() to get access to models.
 */
export class BaseController  {
    protected dispatcher: SlotEventDispatcher;
    constructor() {
        this.dispatcher = getDispatcher(this);
        this.addListeners();
    }

    protected addListeners(): void {

    }
}
