export class DateUtil {
    public static parseServerDate(data: string): string {
        return this.getDate( data ) + "\n" + this.getTime( data );
    }

    public static getDate(data: string): string {
        let date: Date = new Date( data );

        let year: string = date.getFullYear().toString();
        let month: string = this.toDoubleDigitFormat( date.getMonth() + 1 );
        let day: string = this.toDoubleDigitFormat( date.getDate() );

        return year + "-" + month + "-" + day;
    }

    public static getTime(data: string): string {
        let date: Date = new Date( data );
        
        let hours: string = this.toDoubleDigitFormat( date.getHours() );
        let minutes: string = this.toDoubleDigitFormat( date.getMinutes() );
        let seconds: string = this.toDoubleDigitFormat( date.getSeconds() );

        return hours + ":" + minutes + ":" + seconds;
    }

    private static toDoubleDigitFormat(value: number): string {
        return value >= 10 ? value.toString() : "0" + value;
    }

    private static getTimezoneOffset( data: string ) {
        let sign: string = data.substr(19, 1);
        let offset: number = parseInt( data.substr(20, 2) );
        
        return sign === "+" ? offset : offset * -1;
    }
}