import * as PIXI from "pixi.js"
import Container = PIXI.Container;
import {BaseContainer} from "../../baseView/components/BaseContainer";

//TODO: describe component
export class ScrollBarComponent extends Container {

    private _slider: BaseContainer;
    private _background: BaseContainer;

    public get background(): BaseContainer {
        return this._background;
    }

    public set background(value: BaseContainer) {
        this._background = value;
    }

    public get slider(): BaseContainer {
        return this._slider;
    }

    public set slider(value: BaseContainer) {
        this._slider = value;
    }

    public init(): void {
        if (this._background) {
            this.addChild(this._background);
        }
        if (this._slider) {
            this._slider.interactive = true;
            this.addChild(this._slider);
        }

    }

}
