import {BaseAction} from "../../../core/engine/fsm/BaseAction";
import {BackgroundsEvents} from "../events/BackgroundsEvents";

export class AddBackgroundsAction extends BaseAction {
    execute(): Promise<any> {
        return new Promise<any>((resolve) => {
            this.dispatcher.dispatch(BackgroundsEvents.ADD_BACKGROUND_TO_STAGE);
            resolve();
        });
    }
}
