import {BaseAction} from "../../../core/engine/fsm/BaseAction";
import {BackgroundsEvents} from "../events/BackgroundsEvents";
import {BackgroundConstant} from "../events/BackgroundConstant";

export class ShowFreeGameBackgroundAction extends BaseAction{
    execute(): Promise<any> {
        return new Promise<any>((resolve) => {
            this.dispatcher.dispatch(BackgroundsEvents.SHOW_BACKGROUND, BackgroundConstant.FREE_GAME);
            resolve();
        });
    }
}
