import {BaseAction} from "../../../core/engine/fsm/BaseAction";
import {BackgroundsEvents} from "../events/BackgroundsEvents";
import {BackgroundsView} from "../BackgroundsView";
import {AppLogger} from "../../../core/utils/logger/AppLogger";
import {RootViewEvents} from "../../rootView/events/RootViewEvents";

export class ShowRegularBackgroundsAction extends BaseAction {
    execute(): Promise<any> {
        return new Promise<any>((resolve) => {
            this.dispatcher.dispatch(BackgroundsEvents.SHOW_BACKGROUND, "main");
            resolve();
        });
    }
}
