import {BaseMediator} from "../../../core/engine/BaseMediator";
import {ClockView} from "../view/ClockView";
import {ClockEvents} from "../events/ClockEvents";
import {RendererManagerEvents} from "../../renderModule/events/RendererManagerEvents";

export class ClockMediator extends BaseMediator {
    protected view: ClockView;

    addListeners(): void {
        super.addListeners();
        this.dispatcher.addListener(ClockEvents.START_CLOCK, () => {
            this.view.updateTime();
        });
        this.dispatcher.addListener(RendererManagerEvents.RESIZE, this.onResize, this);
    }

    protected onResize(params): void {
        this.view.onResize(params);
    }
}
