import {BaseAction} from "../../../core/engine/fsm/BaseAction";
import {EventDispatcher, LoadEvents} from "general-framework";

export class InitLoaderAction extends BaseAction {
    protected generalDispatcher: EventDispatcher = EventDispatcher.getInstance();
    execute(): Promise<any> {
        return new Promise<any>((resolve) => {
            this.generalDispatcher.dispatch(LoadEvents.INIT_LOADER);
            this.generalDispatcher.dispatch(LoadEvents.RESET_LOADER_VIEW);
            resolve();
        });
    }
}
