import {BaseAction} from "../../../core/engine/fsm/BaseAction";
import {EventDispatcher, LoaderConstants, LoadEvents} from "general-framework";

export class LoadAssetInitAction extends BaseAction {

    protected generalDispatcher: EventDispatcher = EventDispatcher.getInstance();

    execute(): Promise<any> {
        return new Promise<any>((resolve) => {
            this.generalDispatcher.once(LoadEvents.ASSET_LOADED, () => {
                resolve();
            });
            this.generalDispatcher.dispatch(LoadEvents.LOAD_ASSETS, LoaderConstants.INITIAL_PRIORITY);
        });
    }
}
