import {BaseAction} from "../../../core/engine/fsm/BaseAction";
import {AppLogger, EventDispatcher, LoaderConstants, LoadEvents} from "general-framework";


export class LoadSoundGeneralAction extends BaseAction{

    protected generalDispatcher: EventDispatcher = EventDispatcher.getInstance();

    execute(): Promise<any> {
        AppLogger.log(`${this.constructor.name}`);

        return new Promise<any>((resolve) => {
            this.generalDispatcher.addListener(LoadEvents.SOUND_LOADED, () => {
                resolve();
            });
            this.generalDispatcher.dispatch(LoadEvents.LOAD_SOUNDS, LoaderConstants.GENERAL_PRIORITY);
        });
    }
}
