import {BaseAction} from "../../../core/engine/fsm/BaseAction";
import {EventDispatcher, LoadEvents, LoaderConstants} from "general-framework";

export class LoadSoundInitAction extends BaseAction{

    protected generalDispatcher: EventDispatcher = EventDispatcher.getInstance();

    execute(): Promise<any> {
        return new Promise<any>((resolve) => {
            this.generalDispatcher.addListener(LoadEvents.SOUND_LOADED, () => {
                resolve();
            });
            this.generalDispatcher.dispatch(LoadEvents.LOAD_SOUNDS, LoaderConstants.INITIAL_PRIORITY);
        });
    }
}
