import {LoadManager} from "general-framework";
import {SlotAssetsLoader} from "./SlotAssetsLoader";
import {SlotSoundLoader} from "./SlotSoundLoader";
import {SlotBitmapFontsLoader} from "./SlotBitmapFontsLoader";
import {BaseController} from "../../../core/engine/BaseController";
import {createInstance} from "../../../core/engine/createInstance";

export class SlotLoadManager extends BaseController {

    protected assetsLoader: SlotAssetsLoader;
    protected soundLoader: SlotSoundLoader;
    protected bitmapFontsLoader: SlotBitmapFontsLoader;
    protected loadManager: LoadManager;

    constructor() {
        super();
        this.loadManager = createInstance(LoadManager);
        this.loadManager["assetsLoader"] = createInstance(SlotAssetsLoader);
        this.loadManager["soundLoader"] = createInstance(SlotSoundLoader);
        this.loadManager["bitmapFontsLoader"] = createInstance(SlotBitmapFontsLoader);
    }


}
