import {BaseMediator} from "../../../core/engine/BaseMediator";
import {YesNoPopupView} from "../views/YesNoPopupView";
import {ModalPopupEvents} from "../events/ModalPopupEvents";

export class YesNoPopupMediator extends BaseMediator {

    protected view: YesNoPopupView;

    addListeners(): void {
        super.addListeners();
        this.dispatcher.addListener(ModalPopupEvents.SHOW_YES_NO_POPUP, () => {
            this.view.show();
        });
        this.dispatcher.addListener(ModalPopupEvents.HIDE_YES_NO_POPUP, () => {
            this.view.hide();
        });
        this.registerViewListener(ModalPopupEvents.YES_BUTTON_PRESSED);
        this.registerViewListener(ModalPopupEvents.NO_BUTTON_PRESSED);
    }
}
