import {ApplicationModule} from "../../core/engine/ApplicationModule";
import {bindMediator} from "../../core/engine/createInstance";
import {PreloaderMediator} from "./mediator/PreloaderMediator";
import {PreloaderView} from "./view/PreloaderView";
import {DeviceInfoService} from "general-framework";
import {PreloaderPortraitMediator} from "./mediator/PreloaderPortraitMediator";
import {PreloaderPortraitView} from "./view/PreloaderPortraitView";

export class PreloaderModule extends ApplicationModule {
    public execute(): void {
        super.execute();
        bindMediator(PreloaderMediator, PreloaderView, "loaderContainer");
        if (DeviceInfoService.isMobile) {
            bindMediator(PreloaderPortraitMediator, PreloaderPortraitView, "loaderPortraitContainer");
        }
    }
}
