import {PreloaderMediator} from "./PreloaderMediator";
import {DeviceInfoService} from "general-framework";

export class PreloaderPortraitMediator extends PreloaderMediator {

    protected checkDeviceMode(): void {
        if (!DeviceInfoService.isLandscape) {
            this.canShow && this.view.show();
        } else {
            this.view.hide();
        }
    }
}
