import {BaseView} from "../../core/engine/BaseView";
import {BaseRectangle} from "../../core/baseView/components/BaseRectangle";
import {createNewView} from "../../core/engine/createInstance";
import {DeviceInfoService} from "general-framework";

export class SafeAreaView extends BaseView {
    private safeArea: BaseRectangle;

    public init(): void {
        super.init();
        if (DeviceInfoService.isDesktop) {
            this.safeArea = createNewView("safeAreaRectangleDesktop") as BaseRectangle;
        } else {
            this.safeArea = createNewView("safeAreaRectangleMobile") as BaseRectangle;
        }
    }

    public getSafeArea(): BaseRectangle {
        return this.safeArea;
    }

}
