import {GameLogoView} from "../view/GameLogoView";
import {BaseMediator} from "../../../core/engine/BaseMediator";
import {RootViewEvents} from "../events/RootViewEvents";
import {RendererManagerEvents} from "../../renderModule/events/RendererManagerEvents";
import {IRenderViewParams} from "../../renderModule/IRenderViewParams";

export class GameLogoMediator extends BaseMediator {
    protected view: GameLogoView;

    addListeners(): void {
        super.addListeners();
        this.dispatcher.addListener(RootViewEvents.SHOW_GAME_LOGO, this.showView.bind(this));
        this.dispatcher.addListener(RootViewEvents.HIDE_GAME_LOGO, this.hideView.bind(this));
        this.dispatcher.addListener(RendererManagerEvents.RESIZE, this.onResize.bind(this));
    }

    protected showView(): void {
        this.view.show();
    }
    protected hideView(): void {
        this.view.hide();
    }
    protected onResize(params: IRenderViewParams): void {
        this.view.onResize(params);
    }
}
