import {BaseView} from "../../../core/engine/BaseView";
import {IRenderViewParams} from "../../renderModule/IRenderViewParams";
import {DeviceInfoService} from "general-framework";
import {BaseSprite} from "../../../core/baseView/components/BaseSprite";

export class GameLogoView extends BaseView {
    protected  landscapeLogo: BaseSprite;
    protected  portraitLogo: BaseSprite;


    protected initSourceData(): void {
        super.initSourceData();
        this.landscapeLogo = this.source.getChildByName("logoImage");
        this.portraitLogo = this.source.getChildByName("logoPortraitImage");
    }

    public onResize(params: IRenderViewParams) {
        if (DeviceInfoService.isMobile && !DeviceInfoService.isLandscape) {
            if (this.landscapeLogo) {
                this.landscapeLogo.visible = false;
            }
            if (this.portraitLogo) {
                this.portraitLogo.visible = true;
            }
        } else {
            if (this.landscapeLogo) {
                this.landscapeLogo.visible = true;
            }
            if (this.portraitLogo) {
                this.portraitLogo.visible = false;
            }
        }
    }
}
