import {IBaseModel} from "../../../core/engine/interfaces/IBaseModel";

export class CoreSoundModel implements IBaseModel {
    protected _currentAmbientSound: string;
    private static _isSoundOn: boolean;

    get currentAmbientSound(): string {
        return this._currentAmbientSound;
    }

    set currentAmbientSound( value: string ) {
        this._currentAmbientSound = value;
    }

    public static getIsSoundOn(): boolean {
        return this._isSoundOn;
    }

    public static setIsSoundOn(value: boolean) {
        this._isSoundOn = value;
    }
}
