const imagemin = require('imagemin');
const imageminPngquant = require('imagemin-pngquant');
const imageminJpegtran = require('imagemin-jpegtran');

module.exports = async (bufferData) => {
    return await imagemin.buffer(bufferData, {
        plugins: [
            imageminJpegtran(),
            imageminPngquant({
                strip: true,
                quality: [0.5, 0.8]
            })
        ]
    });
};
